mapper.select:
SELECT
    DATETIME_CONVERT("timestamp", '1:MILLISECONDS:EPOCH', '1:MILLISECONDS:EPOCH',
    '#{timePrecision.timeSize}:${timePrecision.timeUnit}') as "timestamp",
    transactionId,
    spanId,
    applicationServiceType,
    applicationName,
    agentId,
    errorClassName,
    errorMessage,
    stackTrace
FROM

WHERE applicationName = #{applicationName}

    AND agentId = #{agentId}


    AND errorClassName = #{spanEventException.errorClassName}
    AND errorMessage = #{spanEventException.errorMessage}
    AND stackTrace = #{spanEventException.stackTrace}

    AND "timestamp" BETWEEN #{range.from} AND #{range.to}
ORDER BY "timestamp" asc
LIMIT ${limit}

SELECT
    DATETIME_CONVERT("timestamp", '1:MILLISECONDS:EPOCH', '1:MILLISECONDS:EPOCH',
    '#{timePrecision.timeSize}:${timePrecision.timeUnit}') as "timestamp",
    transactionId,
    spanId,
    applicationServiceType,
    applicationName,
    agentId,
    errorClassName,
    errorMessage,
    stackTrace
FROM

WHERE applicationName = #{applicationName}

    AND agentId = #{agentId}

    AND transactionId = #{spanEventException.transactionId}
    AND "timestamp" = #{spanEventException.timestamp}
    AND "timestamp" BETWEEN #{range.from} AND #{range.to}
ORDER BY "timestamp" asc
LIMIT ${limit}
